	@Test
	public void toJSON(){
		Map result = new HashMap();
		result.put("abc","ccccccc");
		result.put("AAAAAAAAAAA","CXCXCXcdsafa");
		Gson gson = new Gson();
		//将Java对象转为json字符串，称为“JSON序列化”
		String json = gson.toJson(result);
		System.out.println(json);
	}
	@Test
	//JSON->对象，称为"JSON反序列化"
	public void fromJSON(){
		String json = "{\"abc\":\"ccccccc\",\"AAAAAAAAAAA\":\"CXCXCXcdsafa\"}";
		Gson gson = new Gson();
		//new TypeToken<要转换的对象>(){}.getType()
		//这里的对象可以是Map也可以是具体的Java Bean，只要key与Java bean的属性相同则自动注入
		Map ret = gson.fromJson(json , new TypeToken<Map>(){}.getType());
		System.out.println(ret);

	}
